import * as api from './api'

export function getReturnsList(params) {
  return api.get('/returnsList', params)
}

export function createReturnEntry({
  returnEntry,
  token
}) {
  return api.post('/returnsList', returnEntry, token)
}

export function updateReturnEntry({
  returnId,
  returnEntry,
  token
}) {
  return api.put(`/returnsList/${returnId}`, returnEntry, token)
}

export function getReturnEntry({
  returnId
}) {
  return api.get(`/returnsList/${returnId}`)
}